package forloop;

public class a2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=1;
int b=0;
int n=4;
for (int i = 0; i < n; i++) {
	for (int j = 0; j < n; j++) {
	if (j%2==0) {
		System.out.print(a);
	}
	else {
		System.out.print(b);
	}
	
	}
	System.out.println(" ");
}

	}

}
